CREATE TABLE IF NOT EXISTS "public"."confirmation_create_new_account" (
	"id" int4 NOT NULL GENERATED BY DEFAULT AS IDENTITY ( INCREMENT 1 MINVALUE 1 MAXVALUE 2147483647 START 1 ),
	"created_at" TIMESTAMP ( 6 ) NOT NULL,
	"updated_at" TIMESTAMP ( 6 ) DEFAULT NULL :: TIMESTAMP WITHOUT TIME ZONE,
	"deleted_at" TIMESTAMP ( 6 ) DEFAULT NULL :: TIMESTAMP WITHOUT TIME ZONE,
	"email" VARCHAR ( 255 ) COLLATE "pg_catalog"."default" NOT NULL,
	"language" VARCHAR ( 5 ) COLLATE "pg_catalog"."default" NOT NULL,
	"link_key" VARCHAR ( 16 ) COLLATE "pg_catalog"."default" NOT NULL,
	"expires_at" TIMESTAMP ( 6 ) NOT NULL,
	"is_sent" bool NOT NULL DEFAULT FALSE,
	"remote_addr" VARCHAR ( 45 ) COLLATE "pg_catalog"."default" NOT NULL,
	CONSTRAINT "confirmation_create_new_account_pkey" PRIMARY KEY ( "id" )
);
ALTER TABLE "public"."confirmation_create_new_account" OWNER TO "financelime_user";
COMMENT ON TABLE "public"."confirmation_create_new_account" IS 'Email verification links';

CREATE TABLE IF NOT EXISTS "public"."confirmation_reset_password" (
	"id" int4 NOT NULL GENERATED BY DEFAULT AS IDENTITY ( INCREMENT 1 MINVALUE 1 MAXVALUE 2147483647 START 1 ),
	"created_at" TIMESTAMP ( 6 ) NOT NULL,
	"updated_at" TIMESTAMP ( 6 ) DEFAULT NULL :: TIMESTAMP WITHOUT TIME ZONE,
	"deleted_at" TIMESTAMP ( 6 ) DEFAULT NULL :: TIMESTAMP WITHOUT TIME ZONE,
	"email" VARCHAR ( 255 ) COLLATE "pg_catalog"."default" NOT NULL,
	"language" VARCHAR ( 5 ) COLLATE "pg_catalog"."default" NOT NULL,
	"link_key" VARCHAR ( 16 ) COLLATE "pg_catalog"."default" NOT NULL,
	"expires_at" TIMESTAMP ( 6 ) NOT NULL,
	"remote_addr" VARCHAR ( 45 ) COLLATE "pg_catalog"."default" NOT NULL,
	"is_sent" bool NOT NULL DEFAULT FALSE,
	CONSTRAINT "confirmation_reset_password_pkey" PRIMARY KEY ( "id" )
